INSERT INTO `ingredients` (`id`, `title`, `description`, `created_at`, `updated_at`) VALUES
(1, 'Курица', 'Курица - это разновидность домашней птицы, подвид красной джунглейной птицы (Gallus gallus). Это одно из самых обычных и широко распространенных домашних животных, с общей численностью населения более 19 миллиардов на 2011 год. В мире больше кур, чем любой другой птицы или домашней птицы. Люди держат кур в первую очередь как источник пищи (потребляя как мясо, так и яйца) и, реже, как домашних животных. Первоначально выращенные для петушиных боев или для особых церемоний, цыплята не содержались в пищу до эллинистического периода (4–2 вв. До н.э.).  R  n  r  nГенетические исследования указывают на множественное материнское происхождение в Южной Азии, Юго-Восточной Азии и Восточная Азия, но клады, встречающиеся в Америке, Европе, на Ближнем Востоке и в Африке, происходят с Индийского субконтинента. Из древней Индии одомашненный цыпленок распространился в Лидию в Западной Малой Азии и в Грецию к 5 веку до нашей эры. Птица была известна в Египте с середины 15 века до нашей эры. Согласно анналам Тутмоса III, «птица, которая рожает каждый день», пришла в Египет из земли между Сирией и Сеннаром в Вавилонии.', NULL, NULL),
(2, 'Лосось', 'Лосось - это общее название для нескольких видов рыб из семейства лососевых. Другие рыбы в том же семействе включают форель, голец, хариус и сиг. Лосось произрастает в притоках Северной Атлантики (род Salmo) и Тихого океана (род Oncorhynchus). Многие виды лосося были интродуцированы в неместные среды, такие как Великие озера Северной Америки и Патагония в Южной Америке. Лосось интенсивно разводится во многих частях мира.  R  n  r  n Обычно лосось анадромный: он вылупляется в пресной воде, мигрирует в океан, а затем возвращается в пресную воду для размножения. Однако популяции некоторых видов всю жизнь ограничены пресной водой. Фольклор гласит, что рыба возвращается в то место, где она вылупилась, чтобы нереститься. Исследования слежения показали, что это в основном правда. Часть возвращающегося лосося может заблудиться и нереститься в различных пресноводных системах; процент заблудших зависит от вида лосося. Было показано, что самонаводящееся поведение зависит от обонятельной памяти. Лосось восходит к неогену.', NULL, NULL),
(3, 'Говядина', 'Говядина - это кулинарное название мяса крупного рогатого скота, особенно скелетных мышц. Люди едят говядину с доисторических времен. Говядина является источником высококачественного белка и питательных веществ.  R  n  r  nБольшую часть мяса скелетных мышц говядины можно использовать как есть, просто разрезав на определенные части, такие как жаркое, короткие ребрышки или стейк (филе миньон, стейк из филе , стейк из ромпа, ребристый стейк, стейк рибай, вешалка и т. д.), в то время как другие отрубы обрабатываются (солонина или вяленая говядина). Обрезь, с другой стороны, обычно смешивается с мясом более старшего, более постного (следовательно, более жесткого) крупного рогатого скота, измельчается, измельчается или используется в колбасах. Кровь используется в некоторых разновидностях кровяной колбасы. Другие части, которые съедаются, включают другие мышцы и субпродукты, такие как бычий хвост, печень, язык, рубец из сеточки или рубца, железы (особенно поджелудочная железа и вилочковая железа, называемые сладким хлебом), сердце, мозг (хотя и запрещены там, где это запрещено). существует опасность губчатой ​​энцефалопатии крупного рогатого скота, коровьего бешенства, обычно называемой коровьим бешенством), почек и нежных яичек быка (известных в Соединенных Штатах как телячья картошка, степные устрицы или устрицы Скалистых гор). Некоторые кишки готовят и едят как есть, но чаще их очищают и используют в качестве натуральных колбасных оболочек. Кости используются для приготовления говяжьего бульона.', NULL, NULL),
(4, 'Свинина', 'Свинина - это кулинарное название мяса домашней свиньи (Sus scrofa domesticus). Это наиболее часто потребляемое мясо во всем мире [1], свидетельствующее о том, что свиноводство восходит к 5000 году до нашей эры.  R  n  r  n Свинину едят как в свежеприготовленной, так и в консервированной форме. Посолка продлевает срок хранения продуктов из свинины. Ветчина, копченая свинина, окорок, бекон и колбаса являются примерами консервированной свинины. Колбасные изделия - это отрасль кулинарии, посвященная готовым мясным продуктам, в основном из свинины.  R  n  r  nСвинья - самое популярное мясо в восточных и немусульманских частях Юго-Восточной Азии (Индокитай, Филиппины, Сингапур, Восточный Тимор). и также очень распространен в западном мире, особенно в Центральной Европе. Он высоко ценится в азиатской кухне за жирность и приятную консистенцию. Употребление свинины запрещено еврейским, мусульманским и растафарианским диетическим законом по религиозным причинам, и есть несколько возможных причин.', NULL, NULL),
(5, 'test', 'testTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTesttestTestTest', '2021-05-23 10:31:31', '2021-05-23 10:31:31');

